package Constructor;

public class ConstructorSampleProgram {

	
	int x;
	
	public ConstructorSampleProgram() {
		
		x=5;
	}
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		ConstructorSampleProgram obj = new ConstructorSampleProgram();
		System.out.println(obj.x);
		
	}

}
